// Categorias - Fixed with valid Ionicons names
const categories = [
  { id: "1", name: "Calçados", icon: "footsteps" },
  { id: "2", name: "Relógios", icon: "time-outline" },
  { id: "3", name: "Bolsas", icon: "bag-outline" },
  { id: "4", name: "Eletrônicos", icon: "phone-portrait-outline" },
];

// Banners promocionais
const banners = [
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVBjxQ1J6ptCFtejrtp8mF5D2qWhywbB-ZrQ&s",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
];

const ProductListScreen = () => {
  const [search, setSearch] = useState("");
  const [products, setProducts] = useState([]);
  const router = useRouter();
    
  // buscar produtos
  useEffect(() => {
      fetchProdutos();
  }, []);
  
  const fetchProdutos = async () => {
      try {
          const dados = await productService.getProdutos();
          setProducts(dados);
      } catch (error) {
          console.log("Erro ao buscar produtos:", error);
          // mostrarNotificacao("Erro ao carregar produtos. Tente novamente.", "error");
      }
  };
  // Color Theme based on the provided CSS
  const colors = {
    primary: "#4CAF50",
    primaryDark: "#3e8e41",
    primaryLight: "#f0f9f0",
    danger: "#e53935",
    dangerDark: "#c62828",
    dangerLight: "#ffebee",
    text: "#333",
    textLight: "#666",
    background: "#F8F9FA",
  };

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(search.toLowerCase())
  );

  const renderBanners = () => (
    <FlatList
      data={banners}
      horizontal
      showsHorizontalScrollIndicator={false}
      keyExtractor={(item, index) => index.toString()}
      renderItem={({ item }) => (
        <Image source={{ uri: item }} alt="Promoção" width={Dimensions.get("window").width * 0.9} height={150} m={2} borderRadius={10} />
      )}
    />
  );

  const renderCategories = () => (
    <HStack justifyContent="space-around" mt={4} mb={4}>
      {categories.map((category) => (
        <VStack key={category.id} alignItems="center">
          <Icon as={Ionicons} name={category.icon} size={6} color={colors.primary} />
          <Text fontSize="sm" fontWeight="bold">{category.name}</Text>
        </VStack>
      ))}
    </HStack>
  );

  const renderFooter = () => (
    <>
      <Center my={4}>
        <Text fontSize="lg" fontWeight="bold" color={colors.text}>🛒 Benefícios de comprar conosco:</Text>
        <Text color="gray.600">📦 Frete grátis para compras acima de R$ 200</Text>
        <Text color="gray.600">🛡️ Garantia de 30 dias em todos os produtos</Text>
        <Text color="gray.600">💳 Pagamento seguro com diversos meios</Text>
      </Center>

      <Divider my={4} />

      <Center p={4} bg={colors.primaryLight}>
        <Text fontSize="md" fontWeight="bold" color={colors.text}>Atendimento ao Cliente</Text>
        <Text color="gray.600">📞 Suporte 24h: (11) 9999-9999</Text>
        <Text color="gray.600">📧 Email: suporte@lojaonline.com</Text>
        <Text color="gray.600">📍 Localização: São Paulo, Brasil</Text>
      </Center>
    </>
  );

  const renderHeader = () => (
    <>
      {renderBanners()}
      {renderCategories()}
      <HStack justifyContent="space-between" px={4} pt={3} alignItems="center">
        <Text fontSize="md" color="gray.600">Produtos encontrados:</Text>
        <Badge 
          bg={colors.primaryLight}
          _text={{ color: colors.primary }}
          borderRadius="full" 
          px={3} 
          py={1}
        >
          {filteredProducts.length}
        </Badge>
      </HStack>
    </>
  );

  return (
    <Box flex={1} bg="gray.100">
      {/* Cabeçalho */}
      <Box bg={colors.primary} p={4} pb={6} borderBottomRadius="lg" pt={10}>
        <Heading color="white">CORELINK</Heading>
        <Input
          mt={3}
          placeholder="Buscar produto..."
          variant="filled"
          bg="white"
          borderRadius="full"
          px={4}
          py={2}
          fontSize="md"
          InputLeftElement={<Icon as={Ionicons} name="search" size={5} ml={2} color="gray.500" />}
          value={search}
          onChangeText={setSearch}
        />
      </Box>

      {/* Use apenas um componente de lista principal */}
      <FlatList
        data={filteredProducts}
        keyExtractor={(item) => item.id}
        ListHeaderComponent={renderHeader}
        ListFooterComponent={renderFooter}
        renderItem={({ item }) => (
          <Box bg="white" p={4} mb={4} mx={4} borderRadius="lg" shadow={2}>
            <HStack space={4} alignItems="center">
              <Image source={{ uri: item.image }} alt={item.name} size="xl" borderRadius={10} />
              <VStack flex={1}>
                <Text fontSize="lg" fontWeight="bold">{item.name}</Text>
                <Text fontSize="sm" color="gray.500">{item.description}</Text>
                <HStack alignItems="center" mt={1}>
                  <Icon as={Ionicons} name="star" size={4} color="yellow.400" />
                  <Text ml={1} fontSize="sm">{item.rating}</Text>
                </HStack>
                <Text fontSize="md" color={colors.primary} fontWeight="bold">{item.price}</Text>
                <Button 
                  mt={2} 
                  bg={colors.primary}
                  _pressed={{ bg: colors.primaryDark }}
                  leftIcon={<Icon as={Ionicons} name="cart-outline" size="sm" />}
                  onPress={() => router.push("../carrinho_compra")}
                >
                  Adicionar ao carrinho
                </Button>
              </VStack>
            </HStack>
          </Box>
        )}
      />
    </Box>
  );
};

export default ProductListScreen;